"use strict";

const gulp = require("gulp");
const path = require("path");
const $ = require("@solarwinds/buildmeister/gulp-load-plugins")(gulp);

gulp.task("copy:runtime-scripts", ()=> {
    return gulp.src("../node_modules/angular-mocks/angular-mocks.js", {base: "../node_modules/angular-mocks/"})
        .pipe($.concat("runtime.js"))
        .pipe(gulp.dest(path.resolve("sdk/runtime/js")));
});
