(function() {
    "use strict";

    Xui.module.controller("DemoButtonController", controller, ["$timeout", "xuiToastService"]);

    function controller($timeout, toast) {
        var vm = this;

        vm.displayStyle = 'primary';
        vm.isBusyS = false;
        vm.isBusyF = false;
        vm.doBusyTask = doBusyTask;

        activate();

        //#region Internal Methods

        function activate() {
        }

        function doBusyTask(t) {
            if (t < 250) {
                vm.isBusyF = true;
            }
            else {
                vm.isBusyS = true;
            }

            $timeout(function() {
                vm.isBusyS = false;
                vm.isBusyF = false;
                toast.success("Task completed!");
            }, t);

        }
        //#endregion
    }
})();
