(function() {
    "use strict";

    Xui.controller("DemoClipboardController",
        controller, ["xuiToastService"]);
    function controller(toast) {
        var vm = this;

        vm.textToCopy = "text to copy";

        vm.copyText = function() {
            return vm.textToCopy;
        }

        vm.onClipboardSuccess = function() {
            toast.success("Text was copied to clipboard.");
        };

        vm.onClipboardError = function(ex) {
            toast.error(ex.message);
        };
    }
})();

