(function() {
    "use strict";

    Xui.controller("DemoIfShowController", controller, []);

    function controller() {
        var vm = this;

        vm.initDate = null;
        vm.visible = false;
        vm.toggleVisibility = function () {
            vm.visible = !vm.visible;
        };
        vm.onContentInit = function () {
            vm.initDate = new Date();
        };
    }
})();
