(function () {
    "use strict";

    Xui.module.controller("DemoPagerController", pagerController, ["xuiToastService"]);

    function pagerController(toast) {
        var vm = this;
        vm.totalItems = function() {
          return 1000;
        };
        vm.pagerClicked = function (page, pageSize, total) {
            toast.success("Page:" + page, "PageSize:" + pageSize, "Total:" + total);
        };
    }

})();
