(function() {
    "use strict";

    Xui.controller("SelectorDemoController", controller, ["xuiToastService"]);

    function controller(toast) {
        var vm = this;

        vm.onItemsClick = function (item) {
            console.log(item.value);
        };

        vm.onCheckboxClick = function() {
            toast.success("Checkbox clicked");
        };

        vm.checked = "false";
        vm.indeterminate = "true";
    }
})();
