(function() {
    "use strict";

    Xui.controller("TimeframepickerDemoController", timeframepickerController);
    function timeframepickerController() {
        var vm = this;

        vm.presets = {
            "lastHour": { name: "Last hour", startDatetimePattern: { hours: -1 },  endDatetimePattern: {} },
            "last12Hours": { name: "Last 12 hours", startDatetimePattern: { hours: -12},  endDatetimePattern: {} }
        };
        vm.model3 = {
             startDatetime: new Date("2012-08-08T13:00:00"),
             endDatetime: new Date("2015-07-07T13:00:00")
        };
        vm.model4 = {
            selectedPresetId: "last12Hours"
        };
        vm.datetimeTitleFormat = "MMM D, YYYY";

        vm.dateHasChanged = function(model) {
            console.log('date has changed:'  + model.title);
        }
    }

})();
