(function () {
    "use strict";

    Xui.module.controller("DemoToolbarController", toolbarController, ["xuiToastService"]);

    function toolbarController(toast) {

        var vm = this;

        vm.toolbarButtonClicked = function (msg) {
            toast.success("Clicked toolbar item: " + msg);
        };

        vm.onMenuItemAction = function () {
            toast.success("Menu item clicked");
        };
    }

})();
