import {browser, by, promise} from "@solarwinds/uzi/protractor";
import Helpers from "../helpers";
import {Eyes} from "eyes.protractor";
import {ButtonAtom} from "../index.atom";

describe("Visual tests: Button", function() {
    let eyes: Eyes;
    let button: ButtonAtom;
    beforeEach(() => {
        eyes = Helpers.prepareEyes();
        Helpers.prepareBrowser("button");
    });

    it("Default look", function() {
        eyes.open(browser, "XUI", "Button");
        eyes.checkWindow("Default");
        browser.actions()
            .mouseMove(browser.driver.findElement(by.xpath("//*[@id='button_004']/div")))
            .perform();
        eyes.checkWindow("Mouseover");
        eyes.close();
    });
});
