import {browser, by, promise} from "@solarwinds/uzi/protractor";
import Helpers from "../helpers";
import {Eyes} from "eyes.protractor";
import {ChicletsAtom} from "../index.atom";

describe("Visual tests: Chiclets", function() {
    let eyes: Eyes;
    let chiclets: ChicletsAtom;
    beforeEach(() => {
        eyes = Helpers.prepareEyes();
        Helpers.prepareBrowser("chiclets");
    });

    it("Default look", function() {
        eyes.open(browser, "XUI", "Chiclets");
        eyes.checkWindow("Default");
        browser.actions()
            .mouseMove(browser.driver.findElement(
                by.xpath("//*[@id='chiclets-page']/div/div[1]/div/div/div[1]/span[1]/span[2]/span[1]/span")))
            .perform();
        eyes.checkWindow("MouseOver filter");
        browser.actions()
            .click(browser.driver.findElement(
                by.xpath("//*[@id='chiclets-page']/div/div[1]/div/div/div[1]/span[1]/span[2]/span[1]/span")))
            .perform();
        eyes.checkWindow("Removed one filter");
        eyes.close();
    });
});
