import {by, element, promise} from "@solarwinds/uzi/protractor";
import Helpers from "../helpers";
import {ContentAtom, ScrollbarAtom} from "../index.atom";
import {Atom} from "@solarwinds/uzi";

//region Test cases
describe("USERCONTROL content: ", () => {
    let atom: ContentAtom;

    beforeEach(() => {
        Helpers.prepareBrowser("content");
        const section = element(by.id("content-page"));
        atom = Atom.findIn(ContentAtom, section);
    });

    it("should be clickable radiobutton inside", () => {
        const testSection = element(by.id("xui-demo-content-list-of-checkboxes-large"));
        const input = testSection.element(by.xpath("//*[@class='xui-content-transclude']/ul/li[16]//input"));
        const scrollbarAtom = Atom.findIn(ScrollbarAtom, testSection);
        expect(input.isDisplayed()).toEqual(true);
        input.click();
        expect(input.isEnabled()).toEqual(true);
    });

    it("should have scrollbar", () => {
        const testSection = element(by.id("xui-demo-content-text"));
        const content = testSection.element(by.className("xui-content"));
        const scrollbarAtom = Atom.findIn(ScrollbarAtom, content);
        expect(scrollbarAtom).toBeDefined();
        expect(scrollbarAtom.hasScrollbar(content)).toBe(true);
    });
});
//endregion
