import {browser, promise} from "@solarwinds/uzi/protractor";
import Helpers from "../helpers";
import {Eyes} from "eyes.protractor";
import {ContentAtom} from "../index.atom";

describe("Visual tests: Content", function() {
    let eyes: Eyes;
    let content: ContentAtom;
    beforeEach(() => {
        eyes = Helpers.prepareEyes();
        Helpers.prepareBrowser("content");
    });

    it("Default look", function() {
        eyes.open(browser, "XUI", "Content");
        eyes.checkWindow("Default");
        eyes.close();
    });
});
