import {browser, by, promise} from "@solarwinds/uzi/protractor";
import Helpers from "../helpers";
import {Eyes} from "eyes.protractor";
import {DatetimePickerAtom} from "../index.atom";

describe("Visual tests: DatetimePicker", function() {
    let eyes: Eyes;
    let datetimePicker: DatetimePickerAtom;
    beforeEach(() => {
        eyes = Helpers.prepareEyes();
        Helpers.prepareBrowser("datetimePicker");
    });

    it("Default look", function() {
        eyes.open(browser, "XUI", "DateTimePicker");
        eyes.checkWindow("Default");
        browser.driver.findElement(by.xpath("//*/input[@name='datepicker_003_input']")).click();
        eyes.checkWindow("Opened date");
        eyes.close();
    });
});
