import {by, element, promise} from "@solarwinds/uzi/protractor";
import Helpers from "../helpers";

describe("INTERNALCONTROL Diagnostics", () => {
    beforeEach(() => {
        Helpers.prepareBrowser("diagnostics");
    });

    it("should show angular watch count", () => {
        expect(element(by.id("angularWatchCount")).isPresent()).toBe(true);
    });

    it("should show current language", () => {
        expect(element(by.id("currentLanguage")).isPresent()).toBe(true);
    });
});
