import {browser, by, promise} from "@solarwinds/uzi/protractor";
import Helpers from "../helpers";
import {Eyes} from "eyes.protractor";
import {DialogAtom} from "../index.atom";

describe("Visual tests: Dialog", function() {
    let eyes: Eyes;
    let dialog: DialogAtom;
    beforeEach(() => {
        eyes = Helpers.prepareEyes();
        Helpers.prepareBrowser("dialog");
    });

    it("Default look", function() {
        eyes.open(browser, "XUI", "Dialog");
        eyes.checkWindow("Default");
        browser.driver.findElement(by.xpath("//*[text()[contains(.,'Show Message')]]")).click();
        eyes.checkWindow("Dialog");
        eyes.close();
    },90000);
});
