import {promise} from "@solarwinds/uzi/protractor";
import {DividerAtom} from "../index.atom";
import Helpers from "../helpers";
import {Atom} from "@solarwinds/uzi";

describe("USERCONTROL divider", () => {

    beforeEach(() => {
        Helpers.prepareBrowser("divider");
    });

    it("should display a top border", () => {
        let atom = Atom.find(DividerAtom, "mockDivider1");
        expect(atom.getBorderTopValue()).toContain("1px solid");
    });

    it("should be 2 dividers on the page", () => {
        const expectedDivCount = 2;
        const actualDivCountPromise = Atom.findCount(DividerAtom);
        expect(actualDivCountPromise).toBe(expectedDivCount);
    });
});
