import {DividerAtom} from "../index.atom";

describe("Visual tests: Divider", function() {
    let eyes: Eyes;
    let divider: DividerAtom;
    beforeEach(() => {
        eyes = Helpers.prepareEyes();
        Helpers.prepareBrowser("divider");
    });

    it("Default look", function() {
        eyes.open(browser, "XUI","Divider");
        eyes.checkWindow("Default");
        eyes.close();
    });
});
