import {browser, by, element, promise} from "@solarwinds/uzi/protractor";
import Helpers from "../helpers";
import {Atom} from "@solarwinds/uzi";
import {CheckboxAtom} from "../index.atom";

describe("USERCONTROL Dragdrop >", () => {
    beforeEach(() => {
        Helpers.prepareBrowser("dragdrop");

        const dragStart = Atom.find(CheckboxAtom, "dragStartEvent");
        const dragEnd = Atom.find(CheckboxAtom, "dragEndEvent");
        const dragOver = Atom.find(CheckboxAtom, "dragOverEvent");
        const dragEnter = Atom.find(CheckboxAtom, "dragEnterEvent");
        const dragLeave = Atom.find(CheckboxAtom, "dragLeaveEvent");
    });

    describe("dragdrop >", ()=> {
        // Chromedriver currently does not support html 5 drag/drop
        xit("should be able to drop object", () => {
            const dragElement = browser.driver.findElement(by.id("dropSrc"));
            const dropElement = browser.driver.findElement(by.id("dropTarget"));

            browser.actions().dragAndDrop(dragElement, dropElement).perform();
            expect(element(by.id("dropData")).getText()).toBe("foo");
        });
    });
});
