import {by, element, promise} from "@solarwinds/uzi/protractor";
import Helpers from "../helpers";
import {Atom} from "@solarwinds/uzi";
import {MessageAtom} from "../index.atom";

//region Test cases
describe("USERCONTROL footer: ", () => {
    let atom: MessageAtom;

    beforeEach(() => {
        Helpers.prepareBrowser("footer");
        atom = Atom.find(MessageAtom, "theMessage");
    });

    it("shows transcluded element", () => {
        expect(atom.getContent()).toEqual("I am transcluded");
    });

    it("should have footer", () => {
        const footer = element(by.id("footer"));
        expect(footer.isPresent());
    });
});
//endregion
