import {by, element, promise} from "@solarwinds/uzi/protractor";
import {Atom} from "@solarwinds/uzi";

export class HelpHintAtom extends Atom {
    public static CSS_CLASS = "xui-helphint";

    public static byId = (elementId: string): HelpHintAtom => {
        return new HelpHintAtom(element(by.id(elementId)));
    };

    public getHelpText() {
        return super.getElement().getText();
    }
}
