import {promise} from "@solarwinds/uzi/protractor";
import Helpers from "../helpers";
import {HelpHintAtom} from "../index.atom";

describe("USERCONTROL helpHint", () => {
    let atom: HelpHintAtom;

    beforeEach(() => {
        Helpers.prepareBrowser("helpHint");
        atom = HelpHintAtom.byId("hintID");
    });

    it("should display hint", () => {
        const text = atom.getHelpText();
        expect(text).toBe("helpful");
    });
});
