import {by, promise} from "@solarwinds/uzi/protractor";
import {Atom} from "@solarwinds/uzi";

export class IfShowAtom extends Atom {
    public static CSS_CLASS = "xui-if-show";

    public ngIfIsPresent(): promise.Promise<boolean> {
        return super.getElement().element(by.className("xui-if-show__ng-if")).isPresent();
    }

    public ngIfIsDisplayed(): promise.Promise<boolean> {
        return super.getElement().element(by.className("xui-if-show__ng-if")).isDisplayed();
    }

    public ngShowIsPresent(): promise.Promise<boolean> {
        return super.getElement().element(by.className("xui-if-show__ng-show")).isPresent();
    }

    public ngShowIsDisplayed(): promise.Promise<boolean> {
        return super.getElement().element(by.className("xui-if-show__ng-show")).isDisplayed();
    }

    public getText(): promise.Promise<string> {
        return super.getElement().element(by.className("xui-if-show__ng-show")).getText();
    }
}
