import {promise} from "@solarwinds/uzi/protractor";
import {Atom} from "@solarwinds/uzi";

export class ImageAtom extends Atom {
    public static CSS_CLASS = "xui-image";

    public getName(): promise.Promise<string> {
        return super.getElement().getAttribute("image");
    };

    public getSize(): promise.Promise<string> {
        return super.getElement().getCssValue("width");
    }

    public getFloatings(): promise.Promise<string> {
        return super.getElement().getCssValue("float");
    }

}
