import {browser, by, promise} from "@solarwinds/uzi/protractor";
import Helpers from "../helpers";
import {Eyes} from "eyes.protractor";
import {MenuAtom} from "../index.atom";

describe("Visual tests: menu", function() {
    let eyes: Eyes;
    let menu: MenuAtom;
    beforeEach(() => {
        eyes = Helpers.prepareEyes();
        Helpers.prepareBrowser("menu");
    });

    it("Default look", function() {
        eyes.open(browser, "XUI","Menu");
        eyes.checkWindow("Default");
        browser.driver.findElement(by.xpath("//*[text()[contains(.,'Menu')]]")).click();
        eyes.checkWindow("Opened menu");
        eyes.close();
    });
});
