import {browser, promise} from "@solarwinds/uzi/protractor";
import Helpers from "../helpers";
import {Eyes} from "eyes.protractor";
import {PageContentAtom} from "../index.atom";

describe("Visual tests: PageContent", function() {
    let eyes: Eyes;
    let pageContent: PageContentAtom;
    beforeEach(() => {
        eyes = Helpers.prepareEyes();
        Helpers.prepareBrowser("pageContent");
    });

    it("Default look", function() {
        eyes.open(browser, "XUI","PageContent");
        eyes.checkWindow("Default");
        eyes.close();
    });
});
