import {browser, by, element, promise} from "@solarwinds/uzi/protractor";
import Helpers from "../helpers";
import {Eyes} from "eyes.protractor";
import {PagerAtom} from "../index.atom";

describe("Visual tests: Pager", function() {
    let eyes: Eyes;
    let pager: PagerAtom;
    beforeEach(() => {
        eyes = Helpers.prepareEyes();
        Helpers.prepareBrowser("pager");
        const parentId = "xui-demo-components-pager";
        pager = PagerAtom.findIn(PagerAtom, element(by.id(parentId)));
    });

    it("Default look", function() {
        eyes.open(browser, "XUI","Pager");
        eyes.checkWindow("Default");
        pager.ellipsisLinkClick(0);
        eyes.checkWindow("Pages");
        browser.driver.findElement(by.xpath("//*[@id='pageNumberMenu1']")).click();
        eyes.checkWindow("Items per page");
        eyes.close();
    });
});
