import {browser, by, promise} from "@solarwinds/uzi/protractor";
import Helpers from "../helpers";
import {Eyes} from "eyes.protractor";
import {PanelAtom} from "../index.atom";

describe("Visual tests: Panel", function() {
    let eyes: Eyes;
    let panel: PanelAtom;
    beforeEach(() => {
        eyes = Helpers.prepareEyes();
        Helpers.prepareBrowser("panel");
    });

    it("Default look", function() {
        eyes.open(browser, "XUI","Panel");
        eyes.checkWindow("Default");
        browser.driver.findElement(by.className("xui-panel__heading-btn")).click();
        eyes.checkWindow("Closed");
        eyes.close();
    });
});
