import {browser, by, promise} from "@solarwinds/uzi/protractor";
import Helpers from "../helpers";
import {Eyes} from "eyes.protractor";
import {RadioAtom} from "../index.atom";

describe("Visual tests: Radio", function() {
    let eyes: Eyes;
    let radio: RadioAtom;
    beforeEach(() => {
        eyes = Helpers.prepareEyes();
        Helpers.prepareBrowser("radio");
    });

    it("Default look", function() {
        eyes.open(browser, "XUI","Radio");
        eyes.checkWindow("Default");
        browser.actions()
            .mouseMove(browser.driver.findElement(by.className("xui-radio-mark")))
            .perform();
        eyes.checkWindow("Mouseover non-selected");
        browser.driver.findElement(by.className("xui-radio-mark")).click();
        eyes.checkWindow("Mouseover selected");
        eyes.close();
    });
});
