import { Atom } from "@solarwinds/uzi";
import { ILocation } from "@solarwinds/uzi/webdriver";

export class ResizableAtom extends Atom {

    public static CSS_CLASS = "xui-resizer";

    public offset: ILocation = {
        x: 0,
        y: 0
    };

    public setOffset(x: number, y: number): void {
        this.offset.x = x;
        this.offset.y = y;
    }

}
