import {by, element, promise} from "@solarwinds/uzi/protractor";
import {ScrollbarAtom} from "../index.atom";
import Helpers from "../helpers";
import {Atom} from "@solarwinds/uzi";

describe("USERCONTROL scrollbar: ", () => {
    let atom: ScrollbarAtom;
    let parentEl: any;
    const parentId = "scrollbarMockHV";

    beforeEach(() => {
        Helpers.prepareBrowser("scrollbar");
        parentEl = element(by.id(parentId));
        atom = Atom.findIn(ScrollbarAtom, parentEl);
    });

    it("should have scrollbar", () => {
        expect(atom.hasScrollbar(parentEl)).toBe(true);
    });
});
