import {browser, promise} from "@solarwinds/uzi/protractor";
import Helpers from "../helpers";
import {Eyes} from "eyes.protractor";
import {ScrollbarAtom} from "../index.atom";

describe("Visual tests: Scrollbar", function() {
    let eyes: Eyes;
    let scrollbar: ScrollbarAtom;
    beforeEach(() => {
        eyes = Helpers.prepareEyes();
        Helpers.prepareBrowser("scrollbar");
    });

    it("Default look", function() {
        eyes.open(browser, "XUI","Scrollbar");
        eyes.checkWindow("Default");
        eyes.close();
    });
});
