import {browser, by, promise} from "@solarwinds/uzi/protractor";
import Helpers from "../helpers";
import {Eyes} from "eyes.protractor";
import {SearchAtom} from "../index.atom";

describe("Visual tests: Search", function() {
    let eyes: Eyes;
    let search: SearchAtom;
    beforeEach(() => {
        eyes = Helpers.prepareEyes();
        Helpers.prepareBrowser("search");
    });

    it("Default look", function() {
        eyes.open(browser, "XUI","Search");
        eyes.checkWindow("Default");
        browser.driver.findElement(by.className("xui-search__input-control")).sendKeys("test");
        eyes.checkWindow("Entered text");
        browser.actions().mouseMove(browser.driver.findElement(by.className("xui-search__input-cancel"))).perform();
        eyes.checkWindow("Mouseover cancel button");
        browser.actions().mouseMove(browser.driver.findElement(by.className("xui-search__button"))).perform();
        eyes.checkWindow("Mouseover search button");
        eyes.close();
    });
});
