import {browser, by, promise} from "@solarwinds/uzi/protractor";
import Helpers from "../helpers";
import {Eyes} from "eyes.protractor";
import {SelectorAtom} from "../index.atom";

describe("Visual tests: Selector", function() {
    let eyes: Eyes;
    let selector: SelectorAtom;
    beforeEach(() => {
        eyes = Helpers.prepareEyes();
        Helpers.prepareBrowser("selector");
    });

    it("Default look", function() {
        eyes.open(browser, "XUI","Selector");
        eyes.checkWindow("Default");
        browser.driver.findElement(by.className("xui-selector__action")).click();
        eyes.checkWindow("Selector checked");
        browser.driver.findElement(by.className("xui-selector__toggle")).click();
        eyes.checkWindow("Selector unchecked");
        eyes.close();
    });
});
