import {browser, by, promise} from "@solarwinds/uzi/protractor";
import {Atom} from "@solarwinds/uzi";

export class SidebarAtom extends Atom {
    public static CSS_CLASS = ".xui-sidebar";

    public static isPresent = (): promise.Promise<boolean> => {
        return browser.element(by.css(SidebarAtom.CSS_CLASS)).isPresent();
    };

    public static rightSidebarIsPresent = (): promise.Promise<boolean> => {
        return browser.element(by.css(".xui-sidebar.xui-sidebar-slidein-right")).isPresent();
    };

    public static leftSidebarIsPresent = (): promise.Promise<boolean> => {
        return browser.element(by.css(".xui-sidebar.xui-sidebar-slidein-left")).isPresent();
    };

    public static toggleSidebar = () => browser.element(by.css("[ng-click=\"vm.toggleSidebar()\"]")).click();

    public static setRightPosition = () =>
        browser.findElement(by.xpath("//div[contains(@class, 'xui-radio')]//div[text()='Right']")).click();

    public static setLeftPosition = () =>
        browser.findElement(by.xpath("//div[contains(@class, 'xui-radio')]//div[text()='Left']")).click();
}
