import {browser, by, promise} from "@solarwinds/uzi/protractor";
import Helpers from "../helpers";
import {Eyes} from "eyes.protractor";
import {SorterAtom} from "../index.atom";

describe("Visual tests: Sorter", function() {
    let eyes: Eyes;
    let sorter: SorterAtom;
    beforeEach(() => {
        eyes = Helpers.prepareEyes();
        Helpers.prepareBrowser("sorter");
    });

    it("Default look", function() {
        eyes.open(browser, "XUI","Sorter");
        eyes.checkWindow("Default");
        browser.driver.findElement(by.className("xui-dropdown__toggle-button")).click();
        eyes.checkWindow("Opened sorter");
        browser.driver.findElement(by.className("xui-dropdown__menuitem")).click();
        eyes.checkWindow("Choose item");
        browser.driver.findElement(by.className("xui-dropdown__action")).click();
        eyes.checkWindow("Change sorting way");
        eyes.close();
    });
});
