import {browser, by, promise} from "@solarwinds/uzi/protractor";
import Helpers from "../helpers";
import {Eyes} from "eyes.protractor";

describe("Visual tests: Switch", function() {
    let eyes: Eyes;
    //let switchComponent: SwitchAtom;
    beforeEach(() => {
        eyes = Helpers.prepareEyes();
        Helpers.prepareBrowser("switch");
    });

    it("Default look", function() {
        eyes.open(browser, "XUI","Switch");
        eyes.checkWindow("Default");
        browser.driver.findElement(by.className("xui-switch__bar")).click();
        eyes.checkWindow("Enabled");
        eyes.close();
    });
});
