import {browser, by, promise} from "@solarwinds/uzi/protractor";
import Helpers from "../helpers";
import {Eyes} from "eyes.protractor";
import {TabsAtom} from "../index.atom";

describe("Visual tests: Tabs", function() {
    let eyes: Eyes;
    let tabs: TabsAtom;
    beforeEach(() => {
        eyes = Helpers.prepareEyes();
        Helpers.prepareBrowser("tabs");
    });

    it("Default look", function() {
        eyes.open(browser, "XUI","Tabs");
        eyes.checkWindow("Default");
        browser.actions().mouseMove(browser.driver.findElement(by.className("xui-tabs__header-item"))).perform();
        eyes.checkWindow("Mouseover");
        browser.driver.findElement(by.className("xui-tabs__header-item")).click();
        eyes.checkWindow("First tab");
        eyes.close();
    });
});
