import {browser, by, promise, element} from "@solarwinds/uzi/protractor";
import Helpers from "../helpers";
import {Eyes} from "eyes.protractor";

describe("Visual tests: TimeframePicker", function() {
    let eyes: Eyes;

    beforeEach(() => {
        eyes = Helpers.prepareEyes();
        Helpers.prepareBrowser("timeframepicker");
    });

    it("Default look", function() {
        eyes.open(browser, "XUI","TimeframePicker");
        eyes.checkWindow("Default");
        browser.driver.findElement(by.xpath("//*[text()[contains(.,'Last hour')]]")).click();
        eyes.checkWindow("Opened");
        browser.driver.findElement(by.className("xui-datepicker__textbox")).click();
        eyes.checkWindow("Opened calendar");
        browser.driver.findElement(by.xpath("//*[text()[contains(.,'Quick picks')]]")).click();
        browser.driver.findElement(by.className("xui-dropdown__input")).click();
        eyes.checkWindow("Opened time");
        browser.driver.findElement(by.xpath("//*[text()[contains(.,'9:30 am')]]")).click();
        eyes.checkWindow("Options");
        eyes.close();
    });
});
