import {by, ElementFinder, promise} from "@solarwinds/uzi/protractor";
import {DropdownAtom} from "../dropdown/dropdown.atom";

export class TimepickerAtom extends DropdownAtom {
    public static CSS_CLASS = "xui-time-picker";

    private dropdownElement: ElementFinder;

    constructor(element: ElementFinder) {
        super(element);

        this.dropdownElement = element.element(by.className("xui-dropdown"));
    }

    public isValid(): promise.Promise<boolean> {
        return this.dropdownElement.getAttribute("class").then((result)=> {
            return result.split(/ +/).indexOf("ng-invalid") === -1;
        });
    }

}
