import {by, promise, ElementFinder} from "@solarwinds/uzi/protractor";
import {Atom} from "@solarwinds/uzi";

export class ToolbarAtom extends Atom {
    public static CSS_CLASS = "xui-toolbar";

    // Returns toolbar item by index
    public getItem(index = 0): ElementFinder {
        return super.getElement().all(by.className("xui-toolbar-item")).get(index);
    }

    // Returns whether toolbar is visible
    public isVisible(): promise.Promise<boolean> {
        return super.getElement().isDisplayed();
    }
}
