import {by, element, promise} from "@solarwinds/uzi/protractor";
import Helpers from "../helpers";
import {Atom} from "@solarwinds/uzi";
import {ToolbarAtom} from "../index.atom";

describe("USERCONTROL toolbar", () => {
    let atom: ToolbarAtom;

    beforeEach(() => {
        Helpers.prepareBrowser("toolbar");

        atom = Atom.findIn(ToolbarAtom, element(by.id("toolbar-page")));
    });

    it("check that toolbar is rendered correctly (smoke test)", () => {
        let itemLeft = atom.getItem();

        expect(itemLeft.getText()).toContain("Hey, this is left part of toolbar:");

        let itemRight = atom.getItem(4);

        expect(itemRight.getText()).toContain("Hey, this is right part of toolbar:");
    });
});
