import {promise} from "@solarwinds/uzi/protractor";
import Helpers from "../helpers";
import {Atom} from "@solarwinds/uzi";
import {ButtonAtom, WizardAtom} from "../index.atom";
import WizardTests from "./wizardTests";

describe("USERCONTROL Wizard-Dialog", () => {
    beforeEach(() => {
        Helpers.prepareBrowser("wizard");

        const btn = Atom.find(ButtonAtom, "startWizardDialog");
        btn.click();

        WizardTests.wizard = WizardAtom.fromDialog();
    });

    WizardTests.run();
});
