import SpecHelper from "../../specHelper";

describe("components >", () => {
    describe("accordion >", () => {

        let element:ng.IAugmentedJQuery,
            $compile:ng.ICompileService,
            scope:ng.IScope;

        beforeEach(angular.mock.module("xui"));
        beforeEach(inject((_$compile_:ng.ICompileService, $rootScope:ng.IRootScopeService)=> {
            scope = <ng.IScope>$rootScope.$new();
            $compile = _$compile_;
        }));

        function compileDirective(html:string) {
            element = SpecHelper.getCompiledElement($compile, scope, html);
        }

        const directive = "<uib-accordion><uib-accordion-group><xui-accordion-heading></xui-accordion-heading>" +
            "</uib-accordion-group></uib-accordion>";

        it("compiles", function () {
            compileDirective(directive);
            expect(element.find(".xui-accordion-heading-transclude").length).toBe(1);
        });
    });
});
