import IActionDrawerController = xui.IActionDrawerController;
import IActionDrawerIcon = xui.IActionDrawerIcon;
import SlidingPositionType = xui.SlidingPositionType;

export class ActionDrawerController implements IActionDrawerController {

    public itemSource: IActionDrawerIcon[];
    public positionDirection: SlidingPositionType;

    constructor() {
        if (!_.isArray(this.itemSource)) {
            throw new Error("Property item-source of component xui-action-drawer required.");
        }

        if (!_.isString(this.positionDirection)) {
            throw new Error("Property position-direction of component xui-action-drawer required.");
        }
    }
}
