import BusyController from "./busy-controller";

describe("components >", () => {
    describe("busy >", () => {
        describe("busy-controller >", () => {
            let $controller:ng.IControllerService;
            let $q:ng.IQService;

            beforeEach(angular.mock.module("xui"));
            beforeEach(inject(($injector:any) => {
                $controller = $injector.get("$controller");
                $q = $injector.get("$q");
            }));

            const getController = ():BusyController => {
                return <BusyController>$controller("xuiBusyController", {
                    $q: $q
                });
            };

            it("should return false from cancel()", () => {
                const busyCtrl = getController();
                expect(busyCtrl.cancel()).toEqual(false);
            });
        });
    });
});
