(function () {
    "use strict";

    Xui.module
        .controller("DemoButtonController", DemoButtonController, ["xuiToastService"]);

    function DemoButtonController(toastService) {
        var vm = this;

        vm.toggleDisabled = false;
        vm.smallName = "testSmallBtn";
        vm.defaultName = "defaultBtn";
        vm.largeName = "largeBtn";
        vm.toolTip = "this is a test button";
        vm.tooltipAppendToBody = true;
        vm.numberValue = 23;

        vm.handleClick = function () {
            console.log('call a service to post data...');
            toastService.success('done!!');
        };
    }
})();

