(function() {
    "use strict";

    Xui.module.controller("DemoCheckboxController", DemoCheckboxController);

    DemoCheckboxController.$inject = ['xuiToastService'];

    function DemoCheckboxController(xuiToastService) {
        var vm = this;

        vm.name = "chkDemo";
        vm.plainModel = false;
        vm.commonModel = false;
        vm.disabledModel = true;
        vm.indeterminate = true;
        vm.timeModel = false;
        vm.actionModel = false;
        vm.validationModel = false;
        vm.validationMessage = "Check this and proceed.";
        vm.action = function () { xuiToastService.success('Action has performed.'); };
        vm.submit = function () { xuiToastService.success('Submit.'); };
    }
})();