(function () {
    "use strict";

    Xui.module
        .controller("DemoChicletsController", DemoChicletsController);

    function DemoChicletsController() {
        var vm = this;

        vm.itemsSource = {};
        vm.flatItemsSource = [];
        vm.allowClearAll = false;

        vm.loadChicletsData = function () {
            vm.itemsSource = {
                "statusId": ["downId", "criticalId", "warningId", "okId"],
                "vendorId": ["ciscoId", "hpId", "uniperId"]
            };
            vm.flatItemsSource = ["downId", "criticalId", "warningId", "okId"];
        };
        
        vm.getItemLabel = function (itemId, groupId) {
            if (itemId && groupId) {
                return _.capitalize(itemId.replace("Id", ""));
            } else if (itemId) {
                return _.capitalize(itemId.replace("Id", ""));
            } else if (groupId) {
                return _.capitalize(groupId.replace("Id", ""));
            } else {
                return "N/A";
            }
        };

        vm.loadChicletsData();
    }
})();
