/**
 * @ngdoc directive
 * @name xui.directive:xuiCodeItem
 * @restrict E
 *
 * @description
 * This component is used for displaying code snippets in a listview. It supports line numbers and custom content
 * after the code snippet.
 *
 * @parameters
 * @param {string} content The source code to be displayed.
 * @param {string=} lang Programming language used (auto-detect if not present) - see
 * https://highlightjs.org/static/demo/ for possible values.
 *
 * @example
 *    <example module="xui">
 *        <file src="src/components/codeItem/docs/codeItem-examples.html" name="index.html"></file>
 *        <file src="src/components/codeItem/docs/codeItem-examples.js" name="script.js"></file>
 *    </example>
 */
export class CodeItem implements ng.IDirective {
    public template = require<string>("./codeItem-directive.html");
    public controller = CodeController;
    public controllerAs = "vm";
    public replace = true;
    public bindToController = {
        "lang": "@",
        "lineNumber": "<",
        "content": "<"
    };
    public transclude = {
        "contentSlot": "?content"
    };
}

class CodeController {
}
