var SpecHelper = require('../../specHelper').default;

describe('components >', function () {
    describe('content >', function () {
        var element, scope, compile;

        beforeEach(angular.mock.module('xui'));

        beforeEach(inject(function($compile, $rootScope) {
            compile = $compile;
            scope = $rootScope.$new();
        }));

        var directive =
            '<xui-content size="small">' +
            '<p><a>sample text</a></p>' +
            '</xui-content>';

        it('should use inner text as element content', function() {
            element = SpecHelper.getCompiledElement(compile, scope, directive);

            var el = element.find('.xui-content-transclude');

            expect(el[0].innerHTML).toMatch('<p .*><a>sample text</a></p>');
        });

        it('should have size class based on "size" attribute', function() {
            element = SpecHelper.getCompiledElement(compile, scope, directive);

            expect(element.hasClass('xui-content-sm')).toBeTruthy();
        });
    });
});
